import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './project.css';

import elephant from '../img/elephant.jpg';
import food from '../img/food.jpg';
import waste from '../img/waste.jpg';
import herbal from '../img/herbal.jpg';
import energy from '../img/energy.jpg';
import pizza from '../img/pizza.jpg'
import Portfolio from '../img/portfolio.png'

export default function Project() {
  
  const projects = [
    {
      title: 'Elephant Incident Reporting System',
      description: 'Elephant Incident Reporting System, a mobile application designed to revolutionize the management of HEC. At its core, the system offers registered users a seamless platform for reporting and sharing elephant-related incidents, fostering community engagement and awareness. Used Technologies : React Native',
      imgSrc: elephant,
      githubLink: 'https://github.com/IT20639662/Elephant-reporter-system.git',
    },
    {
      title: 'Online Food Ordering System',
      description: 'An online food ordering system is software that lets restaurant accept orders online. It typically allows customers to choose and pay for food, then alerts the kitchen when an order is made. This happens without contact between staff and customers. Used Tools and Technologies : ReactJS , NestJS , MongoDB, GitHub , MockFlow ,Figma,Selenium',
      imgSrc: food,
      githubLink: 'https://github.com/IT20639662/Food_Delivery.git',
    },
    {
      title: 'Waste Management System',
      description: 'Waste management system always allows to reduce the waste and get benefits from the recycling the waste as new products .Used Tools and Technologies : ReactJS , NodeJS , MongoDB , GitHub ,MockFlow, Figma , Azure Boards , Selenium , Postman',
      imgSrc: waste,
      githubLink: 'https://github.com/IT20639662/UEE_Final-Project.git',
    },
    {
      title: 'Herbal Heal Shopping Platform',
      description: 'Sellers can add, update, and delete items, while buyers can search and buy items, as well as maintain a cart system. Once an item is purchased, an administrator may manually verify the order and confirm it.Used Tools and Technologies : ReactJS , NodeJS , MongoDB ,GitHub , MockFlow ,Figma, Selenium, Postman',
      imgSrc: herbal,
      githubLink: 'https://github.com/Pabasaraa/HerbalHeal.git',
    },
    {
      title: 'EcoHub',
      description: 'EcoHub is a MERN stack web application designed to provide users with a platform to read articles about energy efficient home appliances and new eco friendly energy sources, purchase products related to energy-efficient home appliances and solar panels, and register for upcoming seminars related to energy-efficient products and technologies. Used Tools and Technologies : ReactJS , NodeJS , MongoDB ,GitHub , MockFlow ,Figma, Selenium , Postman',
      imgSrc: energy,
      githubLink: 'https://github.com/Pabasaraa/EcoHub.git',
    },
    {
      title: 'Food Cafe',
      description: 'It provides users with detailed information about various foods and drinks available at a cafÃ©. Through this website, users can easily explore the menu, learn about the prices, and gain insights into the offerings of the cafe. ',
      imgSrc: pizza,
      githubLink: 'https://github.com/IT20639662/Food-cafe.git',
    },
    {
        title: 'Portfolio',
        description: 'My Portfolio Web Application is a personal digital space designed to showcase my skills, projects, and experiences as a fresh graduate. Built using React, the application offers an interactive and visually appealing interface that reflects my passion for web development and design. ',
        imgSrc: Portfolio,
        githubLink: 'https://github.com/IT20639662/Food-cafe.git',
      },
  ];

  return (
    <>
      <Header />
      <div className="projects-container">
        <h1 className="projects-title">My Projects</h1>
        <div className="projects-grid">
          {projects.map((project, index) => (
            <Card className="project-card" key={index}>
              <Card.Img variant="top" src={project.imgSrc} alt={project.title} />
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Text>{project.description}</Card.Text>
                <Button
                  variant="primary"
                  href={project.githubLink}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View on GitHub
                </Button>
              </Card.Body>
            </Card>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
