import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './project.css';

import elephant from '../img/elephant.jpg';
import food from '../img/food.jpg';
import waste from '../img/waste.jpg';
import herbal from '../img/herbal.jpg';
import energy from '../img/e.jpg';



export default function Project() {
  
  const projects = [
    {
      title: 'Elephant Incident Reporting System',
      description: 'Elephant Incident Reporting System, a mobile application designed to revolutionize the management of HEC. At its core, the system offers registered users a seamless platform for reporting and sharing elephant-related incidents, fostering community engagement and awareness. ',
      imgSrc: elephant,
      githubLink: 'https://github.com/IT20639662/Elephant-reporter-system.git',
    },
    {
      title: 'Online Food Ordering System',
      description: 'An online food ordering system is software that lets restaurant accept orders online. It typically allows customers to choose and pay for food, then alerts the kitchen when an order is made. This happens without contact between staff and customers',
      imgSrc: food,
      githubLink: 'https://github.com/IT20639662/Food_Delivery.git',
    },
    {
      title: 'Waste Management System',
      description: 'Waste management system always allows to reduce the waste and get benefits from the recycling the waste as new products .',
      imgSrc: waste,
      githubLink: 'https://github.com/IT20639662/UEE_Final-Project.git',
    },
    {
      title: 'Herbal Heal Shopping Platform',
      description: 'Sellers can add, update, and delete items, while buyers can search and buy items, as well as maintain a cart system. Once an item is purchased, an administrator may manually verify the order and confirm it.',
      imgSrc: herbal,
      githubLink: 'https://github.com/Pabasaraa/HerbalHeal.git',
    },
    {
      title: 'Project Five',
      description: 'EcoHub is a MERN stack web application designed to provide users with a platform to read articles about energy efficient home appliances and new eco friendly energy sources, purchase products related to energy-efficient home appliances and solar panels, and register for upcoming seminars related to energy-efficient products and technologies',
      imgSrc: 'https://via.placeholder.com/300x180',
      githubLink: 'https://github.com/Pabasaraa/EcoHub.git',
    },
    {
      title: 'Project Six',
      description: 'Personal portfolio website built with React.',
      imgSrc: 'https://via.placeholder.com/300x180',
      githubLink: 'https://github.com/IT20639662/project-six',
    },
  ];

  return (
    <>
      <Header />
      <div className="projects-container">
        <h1 className="projects-title">My Projects</h1>
        <div className="projects-grid">
          {projects.map((project, index) => (
            <Card className="project-card" key={index}>
              <Card.Img variant="top" src={project.imgSrc} alt={project.title} />
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Text>{project.description}</Card.Text>
                <Button
                  variant="primary"
                  href={project.githubLink}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View on GitHub
                </Button>
              </Card.Body>
            </Card>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}
